import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { analyzeSymptoms } from '../../utils/aiHelper';

export default function AIAssistant() {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([
    {
      type: 'bot',
      text: 'Hello! I\'m your AI Health Assistant. üëã\n\nDescribe your symptoms and I\'ll help you understand what might be causing them and suggest the right specialist.',
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = {
      type: 'user',
      text: input,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsTyping(true);

    // Call backend Gemini proxy
    try {
      const resp = await fetch('/api/gemini', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: input })
      });

      if (!resp.ok) throw new Error('Network response was not ok');

      const data = await resp.json();

      // Expect backend to return { analysis: { ... } } or raw text
      const analysis = data.analysis || data;

      const botResponse = {
        type: 'bot',
        analysis: analysis,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, botResponse]);
    } catch (err) {
      console.error('Gemini request failed, falling back to local analyzer:', err);
      const analysis = analyzeSymptoms(input);
      const botResponse = { type: 'bot', analysis, timestamp: new Date() };
      setMessages(prev => [...prev, botResponse]);
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <div className="ai-assistant-page">
      <div className="chat-container">
        <div className="chat-header">
          <div className="header-content">
            <div className="bot-avatar">ü§ñ</div>
            <div className="bot-info">
              <h2>AI Health Assistant</h2>
              <p className="status">Online ‚Ä¢ Ready to help</p>
            </div>
          </div>
        </div>

        <div className="chat-messages">
          {messages.map((msg, idx) => {
            const isUser = msg.type === 'user';
            const analysis = msg.analysis;

            // If analysis is a plain string, render it simply.
            if (!isUser && analysis && typeof analysis === 'string') {
              return (
                <div key={idx} className={`message ${msg.type}`}>
                  <div className="message-bubble bot-bubble">
                    <p>{analysis}</p>
                  </div>
                </div>
              );
            }

            // If analysis is an object, provide safe defaults for rendering
            const cond = analysis && (analysis.condition || analysis.title || 'Analysis');
            const causes = analysis && Array.isArray(analysis.causes) ? analysis.causes : [];
            const tips = analysis && Array.isArray(analysis.tips) ? analysis.tips : [];
            const whenToSeeDr = analysis && analysis.whenToSeeDr ? analysis.whenToSeeDr : '';
            const specialists = analysis && Array.isArray(analysis.specialists) ? analysis.specialists : [];
            const urgent = analysis && analysis.urgent;

            return (
              <div key={idx} className={`message ${msg.type}`}>
                {isUser ? (
                  <div className="message-bubble user-bubble">
                    <p>{msg.text}</p>
                  </div>
                ) : analysis ? (
                  <div className="message-bubble bot-bubble analysis">
                    <h3 className={urgent ? 'urgent' : ''}>
                      {urgent && '‚ö†Ô∏è '}{cond}
                    </h3>

                    <div className="analysis-section">
                      <h4>üí° Possible Causes:</h4>
                      <ul>
                        {causes.length ? causes.map((cause, i) => <li key={i}>{cause}</li>) : <li>‚Äî</li>}
                      </ul>
                    </div>

                    <div className="analysis-section">
                      <h4>ü©π Basic Tips:</h4>
                      <ul>
                        {tips.length ? tips.map((tip, i) => <li key={i}>{tip}</li>) : <li>‚Äî</li>}
                      </ul>
                    </div>

                    <div className="analysis-section">
                      <h4>üë®‚Äç‚öïÔ∏è When to See a Doctor:</h4>
                      <p className={urgent ? 'urgent-text' : ''}>{whenToSeeDr || '‚Äî'}</p>
                    </div>

                    <div className="analysis-section">
                      <h4>üè• Suggested Specialists:</h4>
                      <div className="specialist-tags">
                        {specialists.length ? specialists.map((spec, i) => (
                          <span key={i} className="specialist-tag">{spec}</span>
                        )) : <span className="specialist-tag">General Physician</span>}
                      </div>
                    </div>

                    {specialists.length > 0 && (
                      <button
                        onClick={() => navigate('/doctors?specialty=' + specialists[0])}
                        className="btn-book-doctor"
                      >
                        üìÖ Book Doctor Now
                      </button>
                    )}
                  </div>
                ) : (
                  <div className="message-bubble bot-bubble">
                    <p>{msg.text}</p>
                  </div>
                )}
              </div>
            );
          })}

          {isTyping && (
            <div className="message bot">
              <div className="message-bubble bot-bubble typing">
                <span className="dot"></span>
                <span className="dot"></span>
                <span className="dot"></span>
              </div>
            </div>
          )}
        </div>

        <form onSubmit={handleSend} className="chat-input-form">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Describe your symptoms..."
            className="chat-input"
            disabled={isTyping}
          />
          <button type="submit" className="send-btn" disabled={isTyping || !input.trim()}>
            ‚û§
          </button>
        </form>

        <div className="disclaimer">
          <strong>‚ö†Ô∏è Disclaimer:</strong> This is not a medical diagnosis. For accurate diagnosis and treatment, please consult a qualified doctor.
        </div>
      </div>
    </div>
  );
}
